mapper.update:
update review set
content=#{content} 
where rno =#{rno}

mapper.select:
select * from ( select R.* , @rownum:=@rownum+1 as rownum from ( select r.pno,r.rno, r.content, r.rate , r.regdate , r.writer ,ra.filename as fn ,r.rev_subject , (select count(helpfulUserId) from helpful where rno = r.rno) as helpful ,p.pname from review as r left join review_attach as ra on (r.rno = ra.rno) left join product_board as p on (r.pno = p.pno) left join helpful h on (r.rno = h.rno) join (select @rownum:=0) as rn
group by r.pno ) as R order by R.rno desc) as C
where rownum >(#{pageNum}-1) * #{amount} and rownum<= #{amount} * #{pageNum}

		select * from (
	select R.* , @rownum:=@rownum+1 as rownum from (
		select 	r.pno,r.rno, r.content, r.rate , r.regdate , r.writer ,r.rev_subject , (select count(helpfulUserId) from helpful where rno = r.rno) as helpful,p.pname
				from review as r left join product_board as p on (r.pno = p.pno) left join helpful h on (r.rno = h.rno)  join (select @rownum:=0) as rn 
					where r.writer = #{writer}
						
						) 
                        as R order by R.rno desc) as C 
                         
			where rownum >(#{cri.pageNum}-1) * #{cri.amount} and rownum<= #{cri.amount} * #{cri.pageNum}

mapper.insert:
		INSERT INTO helpful(rno,helpfulUserId)

SELECT #{rno},#{uname}

FROM dual

WHERE NOT EXISTS (

 SELECT *  FROM helpful

 WHERE  rno =#{rno} and helpfulUserId = #{uname} 

 )
